<html><head><link href="CSS/basestyle.css" rel="stylesheet" type="text/css" media="screen" /><?php//循环目录下的所有文件function delFileUnderDir($dirName){if($handle = opendir("$dirName")){   while(false !== ($item = readdir($handle))){   if($item != "." && $item != ".."){   if(is_dir("$dirName/$item")){         delFileUnderDir("$dirName/$item");   }else{   if(unlink("$dirName/$item"))echo"亲：自动化测试机已完成初始化操作，受影响路径为： $dirName/$item<br />\n";   }   }   }   closedir($handle);  }}delFileUnderDir("F:/Jetsen_AutoTest_project/SmartJetsen/WebAPP/TestPicture");//删除所有截图文件delFileUnderDir("F:/Jetsen_AutoTest_project/SmartJetsen/WebAPP/TestVideo");//删除所有录屏文件delFileUnderDir("F:/Jetsen_AutoTest_project/SmartJetsen/WebAPP/TestReport");//删除所有测试报告delFileUnderDir("F:/Jetsen_AutoTest_project/SmartJetsen/WebAPP/TestLog");//删除所有测试日志文件delFileUnderDir("F:/Jetsen_AutoTest_project/SmartJetsen/WebAPP/Debug");//删除所有Debug日志文件?></head><body><h1><div>您可以直接关闭该页面</div></h1></body></html>